// eslint-disable-next-line import/no-extraneous-dependencies
import PropTypes from 'prop-types';
import React from 'react';
import styled from 'styled-components';

import {
  animationMixin,
  commonSizingMixin,
  commonStyleMixin,
  cornerRadiusMixin,
  spaceMixin,
} from '../../utils/mixins';

const StyledSkeletonRectangle = styled.div`
  ${commonStyleMixin};
  ${commonSizingMixin};
  ${animationMixin};
  ${cornerRadiusMixin};
  ${spaceMixin};
`;

// const SkeletonRectangle = props => <StyledSkeletonRectangle {...props} />;

export default function SkeletonRectangle(props) {
  return <StyledSkeletonRectangle {...props}>&nbsp;</StyledSkeletonRectangle>;
}

SkeletonRectangle.propTypes = {
  /**
   * For `rect` and `text` types. Ignored on `avatar`.
   */
  height: PropTypes.oneOfType([PropTypes.string, PropTypes.number]),
  /**
   * For `rect` and `text` types. Ignored on `avatar`.
   */
  width: PropTypes.oneOfType([PropTypes.string, PropTypes.number]),
};

SkeletonRectangle.defaultProps = {};
